import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api";

export default function Dashboard(){
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const res = await API.get("/users");
      setUsers(res.data);
    } catch (err) {
      alert("Failed to fetch users");
    } finally {
      setLoading(false);
    }
  };

  useEffect(()=>{ fetchUsers(); }, []);

  const handleDelete = async (id) => {
    if(!window.confirm("Delete this user?")) return;
    try {
      await API.delete(`/users/${id}`);
      setUsers(prev => prev.filter(u => u._id !== id));
    } catch (err) {
      alert("Delete failed");
    }
  };

  return (
    <div>
      <h2>All Users</h2>
      {loading ? <p className="small">Loading...</p> : (
        <>
          {users.length === 0 ? <p className="small">No users yet. Add one.</p> : (
            <table className="table">
              <thead><tr><th>Name</th><th>Email</th><th>Phone</th><th>Company</th><th>Actions</th></tr></thead>
              <tbody>
                {users.map(u => (
                  <tr key={u._id}>
                    <td><Link className="link" to={`/user/${u._id}`}>{u.name}</Link></td>
                    <td>{u.email}</td>
                    <td>{u.phone}</td>
                    <td>{u.company || "-"}</td>
                    <td className="actions">
                      <Link to={`/edit/${u._id}`} className="btn">Edit</Link>
                      <button onClick={()=>handleDelete(u._id)} className="btn" style={{background:'#d9534f'}}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
    </div>
  );
}
